import React, { useEffect, useState, useMemo } from "react"
import { createUseStyles } from "react-jss"
import cn from "classnames"
import { useRootState } from "@/App.js"
import { request } from "@/request.js"
import ClickAway from "@/components/Utils/ClickAway"
import { useCookies } from "react-cookie"

const settingStyle = createUseStyles({
  root: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "flex-start",
    paddingLeft: 30,
    position: "relative",
  },
  bigTitle: {
    width: "100%",
    fontSize: 40,
    borderBottom: "1px solid black",
    fontWeight: 500,
    marginBottom: 10,
  },
  selectionField: {
    display: "flex",
  },
  selectionLabel: {
    fontSize: 20,
  },
  submitBtn: {
    marginTop: 20,
    width: "10%",
    height: 40,
    color: "white",
    backgroundColor: "black",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontWeight: "bold",
    cursor: "pointer",
    fontSize: 20,
  },
})

const mainStyle = createUseStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    position: "relative",
  },
  setting: {
    marginTop: 20,
    width: "10%",
    height: 40,
    color: "white",
    backgroundColor: "grey",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontWeight: "bold",
    fontSize: 20,
    cursor: "pointer",
    position: "absolute",
    right: 0,
  },
})

const Clustering = (props) => {
  // const classes = clusteringStyle()
  const [cookies, setCookie, removeCookie] = useCookies(["cookie-name"])
  console.log(cookies)
  const [currentDisplay, setCurrentDispaly] = useState("data")
  const [data, setData] = useState([])
  const [Dvalue, setDvalue] = useState(cookies.D || 200)
  const [Tvalue, setTvalue] = useState(cookies.T || 3)
  const [Cvalue, setCvalue] = useState(cookies.D || 2)

  const classes= mainStyle()
  const onSettingSaved = (d, t, c) => {
    setDvalue(d)
    setTvalue(t)
    setCvalue(c)
    setCookie("D",d)
    setCookie("T",t)
    setCookie("C",c)
    alert("Setting saved")
    setCurrentDispaly("data")
  }
  const clustering_func = async () => {
    if (!Dvalue || !Tvalue || !Cvalue) {
      alert("Missing some fields")
    } else if (isNaN(Dvalue) || isNaN(Tvalue) || isNaN(Cvalue)) {
      alert("All the fields should be a number.")
    } else {
      await request("findCluster", {
        Dvalue: Dvalue,
        Tvalue: Tvalue,
        Cvalue: Cvalue,
      })
      setDvalue("")
      setTvalue("")
      setCvalue("")
      window.alert("You have successfully found a cluster.")
    }
  }

  useEffect(() => {
    (async() => {
      const result = await request("findCluster", {
        Dvalue: Dvalue,
        Tvalue: Tvalue,
        Cvalue: Cvalue,
      })
      if(result){
        setData(result.clusters)
      }
      console.log(result)
    })()
  },[Dvalue,Tvalue,Cvalue])

  return (
    <>
      {currentDisplay === "data" && 
        <div className={classes.root}>
          <div className={classes.setting} onClick={() => setCurrentDispaly("setting")}> Setting</div>
          {data.length > 0 && !selectCase && (
        <table>
          <thead>
            {headerGroups.map((headerGroup) => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map((column) => (
                  <th {...column.getHeaderProps()}>
                    {column.render("Header")}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map((row) => {
              prepareRow(row)
              return (
                <tr
                  {...row.getRowProps()}
                  onClick={() => {
                    showDetails(row)
                  }}
                  className={classes.row}
                >
                  {row.cells.map((cell) => {
                    return (
                      <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                    )
                  })}
                </tr>
              )
            })}
          </tbody>
        </table>
      )}
        </div>
      }
      {currentDisplay === "setting" && (
        <Setting onSettingSaved={onSettingSaved} />
      )}
    </>
  )
}

const Setting = (props) => {
  const classes = settingStyle()
  const [Dvalue, setDvalue] = useState("")
  const [Tvalue, setTvalue] = useState("")
  const [Cvalue, setCvalue] = useState("")

  const clustering_func = () => {
    if (!Dvalue || !Tvalue || !Cvalue) {
      alert("Missing some fields")
    } else if (isNaN(Dvalue) || isNaN(Tvalue) || isNaN(Cvalue)) {
      alert("All the fields should be a number.")
    } else {
      props.onSettingSaved(Dvalue,Tvalue,Cvalue)
    }
  }
  return (
    <div className={classes.root}>
      <div className={classes.bigTitle}>Setting</div>
      <div className={classes.selectionField}>
        <div className={classes.selectionLabel}>D value(in meter): </div>
        <input value={Dvalue} onChange={(e) => setDvalue(e.target.value)} />
      </div>
      <br />
      <div className={classes.selectionField}>
        <div className={classes.selectionLabel}>T value(in day): </div>
        <input value={Tvalue} onChange={(e) => setTvalue(e.target.value)} />
      </div>
      <br />
      <div className={classes.selectionField}>
        <div className={classes.selectionLabel}>C value(cluster size):</div>
        <input value={Cvalue} onChange={(e) => setCvalue(e.target.value)} />
      </div>
      <br />
      <div
        className={classes.submitBtn}
        onClick={clustering_func}
      >
        Save
      </div>

      <br />
      <br />
      <br />
    </div>
  )
}
export default Clustering
