import React, { useEffect, useState, useMemo } from "react"
import { createUseStyles } from "react-jss"
import cn from "classnames"
import { useRootState } from "@/App.js"
import { request } from "@/request.js"
import ClickAway from "@/components/Utils/ClickAway"

import { useTable } from 'react-table'
import '../../table.css'
import MOCK_DATA from '../../MOCK_DATA.json'


const tableStyle = createUseStyles({
  root: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "flex-start",
    paddingLeft: 30,
    position: "relative",
  },
  bigTitle: {
    width: "100%",
    fontSize: 40,
    borderBottom: "1px solid black",
    fontWeight: 500,
    marginBottom: 10,
  },
  // selectionField: {
  //   display: "flex",
  // },
  // selectionLabel: {
  //   fontSize: 20,
  // },
  // nextPage: {
  //   cursor: "pointer",
  //   textDecoration: " underline",
  //   position: "absolute",
  //   bottom: 0,
  //   right: 20,
  // },
  row: {
    cursor: "pointer",
  },
})

const useCaseDetailStyle = createUseStyles({
  root: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    paddingLeft: 30,
  },
  backBtn: {
    position: "absoulte",
    top: 10,
    left:30,
    border: "1px solid black",
  }
})
const CaseDetail = (props) => {
  const classes = useCaseDetailStyle()
  return (
    <div className={classes.root}>
      <div className={classes.backBtn}>Back</div>
    </div>

  )
}
const ViewRecord = (props) => {
  const [data, setData] = useState([])
  const [selectCase, setSelectedCase] = useState(null)

  const classes = tableStyle()
  const COLUMNS = [
    {
      Header: 'CaseId',
      accessor:'case_no'
    },
    // {
    //   Header:'Patient Name',
    //   accessor:'patient_name'
    // },
    {
      Header:'Patient ID',
      accessor:'patient'
    },
    // {
    //   Header:'Virus Name',
    //   accessor:'virus_name'
    // },
    {
      Header:'virus ID',
      accessor:'virus'
    },
    {
      Header:'Date Confirmed',
      accessor:'date_confirmed'
    },
  ]
  const columns = useMemo(() => COLUMNS, [])

  //const data = useMemo(() => MOCK_DATA,[])//not done
  useEffect(() => {
    (async() => {
      const result = await request('getAllCase',[])
      setData(result)
    })()
  },[])
  // console.log(JSON.stringify(request('viewDetail')))
  const tableInstance = useTable({
    columns,
    data,
  })
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = tableInstance
  console.log(rows)

  const showDetails = async(row) => {
    console.log(row)
    const result = await request('getCaseById',{id:row.values.case_no})
    setSelectedCase(result)
    console.log(result)
  }
  return (
    <div className={classes.root}>
      <div>
        <div className={classes.bigTitle}>View Record</div>
      </div>
      {
        data.length > 0 && !selectCase &&
        <table>
        <thead>
          {/* <tr>
            <th>CaseID</th>
            <th>PatientName</th>
            <th>Virus</th>
            <th>Date Confirmed</th>
            <th>URL for more details</th>
          </tr> */}
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {
                headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                ))
              }
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {/* <tr>
            <td>dummy_ID</td>
            <td>Dummy Chan</td>
            <td>CoVID-19</td>
            <td>dd-mm-yyyy</td>
            <td><a href="https://www.gooogle.com">URL</a></td>
          </tr> */}
          {rows.map((row) => {
            prepareRow(row)
            return (
              <tr {...row.getRowProps()} onClick={() => {showDetails(row)}} className={classes.row}>
                {
                  row.cells.map((cell) => {
                  return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                  })
                }
              </tr>
            )
          })
          }
        </tbody>
      </table>
      }
      {
        selectCase &&
        <CaseDetail />
      }

    </div>

  )
}



export default ViewRecord
