import React, { useEffect, useState } from "react"
import { render } from "react-dom"
import { createUseStyles } from "react-jss"
import cn from "classnames"
import { useRootState } from "@/App.js"
import { request } from "@/request.js"
import ClickAway from "@/components/Utils/ClickAway"

const useInputFormStyle = createUseStyles({
  root: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "flex-start",
    paddingLeft: 30,
    position: "relative",
  },
  selectionTitle: {
    width: "100%",
    fontSize: 40,
    borderBottom: "1px solid black",
    fontWeight: 500,
    marginBottom: 10,
  },
  selectionField: {
    display: "flex",
  },
  selectionLabel: {
    fontSize: 20,
  },
  nextPage: {
    cursor: "pointer",
    textDecoration: " underline",
    position: "absolute",
    bottom: 0,
    right: 20,
  },
  searchBtn: {
    marginLeft: 10,
    cursor: "pointer",
  },
  locationBox: {},
  addBtn: {
    marginTop: 20,
    width: "60%",
    height: 50,
    color: "white",
    backgroundColor: "black",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontWeight: "bold",
    cursor: "pointer",
    fontSize: 25,
  },
  closeBtn: {
    cursor: "pointer",
  },
})
const LocationRecord = (props) => {
  const classes = useInputFormStyle()
  const [category, setCategory] = useState("")
  const [dateFrom, setDateFrom] = useState("")
  const [dateTo, setDateTo] = useState("")
  const [searchTerm, setSearchTerm] = useState("")
  const [result, setResult] = useState("")
  const [deleted, setDeleted] = useState(false)

  const onDateFromChange = (value) => {
    if (category === "visit") {
      setDateTo(value)
    }
    setDateFrom(value)
  }
  const onDateToChange = (value) => {
    if (category === "visit") {
      setDateFrom(value)
    }
    setDateTo(value)
  }
  const searchLocation = async () => {
    if (searchTerm !== "") {
      const result = await request("searchLocation", {
        locationTerm: searchTerm,
      })
      console.log(result)
      setResult(result)
    }
  }
  useEffect(() => {
    const location = { category, dateFrom, dateTo, searchTerm, result }
    console.log(location)
    props.onRecordChange(location)
  }, [category, dateFrom, dateTo, searchTerm, result])

  return (
    <>
      {!deleted && (
        <tr>
          <td>
            <select
              className="category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
            >
              <option value="residence">Residence</option>
              <option value="workplace">Workplace</option>
              <option value="visit">Visit</option>
            </select>
          </td>
          <td>
            <input
              type="date"
              className="dateFrom"
              value={dateFrom}
              onChange={(e) => onDateFromChange(e.target.value)}
            />
          </td>
          <td>
            <input
              type="date"
              className="dateTo"
              value={dateTo}
              onChange={(e) => onDateToChange(e.target.value)}
            />
          </td>
          <td className={classes.locationBox}>
            <input
              type="text"
              className="location"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <span className={classes.searchBtn} onClick={searchLocation}>
              Search
            </span>
          </td>
          <td>
            <i
              className={cn(classes.closeBtn, "fa fa-window-close")}
              onClick={() => {props.onDeleteRow(); setDeleted(true)}}
            ></i>
          </td>
        </tr>
      )}
    </>
  )
}

const LocationInfo = (props) => {
  const [page, setPage] = useState(0)
  const classes = useInputFormStyle()
  const blankrecord = {
    category: "",
    dateFrom: "",
    dateTo: "",
    location: "",
    result: [],
  }
  const [locationRecord, setLocationRecord] = useState([{ ...blankrecord }])
  const addRecord = () => {
    const entry = { ...blankrecord }
    setLocationRecord((locationRecord) => locationRecord.concat(entry))
  }

  const onDeleteRow = (i) => {
    let old = [...locationRecord]
    old[i] = {}
    setLocationRecord(old)
  }

  const onRecordChange = (location, i) => {
    let old = [...locationRecord]
    old[i] = location
    setLocationRecord(old)
  }

  useEffect(() => {
    console.log(locationRecord)
  }, [locationRecord])

  return (
    <div className={classes.root}>
      <div className={classes.selectionTitle}>Add Location Record</div>
      <table className={classes.empTable}>
        <tr>
          <th>Category</th>
          <th>Date From</th>
          <th>Date To</th>
          <th>Location</th>
          <th>Delete</th>
        </tr>
        <tbody>
          {locationRecord.map((val, idx) => {
            if (val !== {})
              return (
                <LocationRecord
                  onRecordChange={(location) => onRecordChange(location, idx)}
                  onDeleteRow={() => onDeleteRow(idx)}
                />
              )
            return <></>
          })}
        </tbody>
      </table>
      <div className={classes.addBtn} onClick={addRecord}>
        Add
      </div>
    </div>
  )
}

export default LocationInfo
