import React, { useEffect, useState, useMemo } from "react"
import { createUseStyles } from "react-jss"
import cn from "classnames"
import { useRootState } from "@/App.js"
import LineInput from "@/components/Utils/LineInput.js"
import { request } from "@/request.js"
import ClickAway from "@/components/Utils/ClickAway"

import { useTable } from 'react-table'
import '../../table.css'


const tableStyle = createUseStyles({
  root: {
    width: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "flex-start",
    paddingLeft: 30,
    position: "relative",
  },
  bigTitle: {
    width: "100%",
    fontSize: 40,
    borderBottom: "1px solid black",
    fontWeight: 500,
    marginBottom: 10,
  },
  selectionField: {
    display: "flex",
  },
  selectionLabel: {
    fontSize: 20,
  },
  nextPage: {
    cursor: "pointer",
    textDecoration: " underline",
    position: "absolute",
    bottom: 0,
    right: 20,
  },
})



const VirusInfo = (props) => {
  const classes = tableStyle()
  const [vname, setVname] = useState("")
  const [disease, setDisease] = useState("")
  const [maxp, setMaxp] = useState("")


  const vinfo = async () => {
      const result = await request("addVinfo",{vname: vname, disease: disease, maxp: maxp})
  }


  const COLUMNS = [
    {
      Header: 'Virus Name',
      accessor:'virus_name'
    },
    {
      Header:'Disease',
      accessor:'disease'
    },
    {
      Header:'Max. Infectious Period (days)',
      accessor:'max_infect_period'
    },
  ]


  return (
    <div className={classes.root}>
      <div className={classes.bigTitle}>Virus Info Page</div>

        <div>Virus Name:</div>
        <LineInput onChange={e => setVname(e.target.value)} /><br />
        <div>Disease:</div>
        <LineInput onChange={e => setDisease(e.target.value)} /><br />
        <div>Max. Infectious Period:</div>
        <LineInput onChange={e => setMaxp(e.target.value)} /><br />
        <div onClick={vinfo}>Submit</div>


      <br /><br /><br />



    </div>
  )
}

export default VirusInfo
