import React, { useEffect, useState } from "react"
import { createUseStyles } from "react-jss"
import cn from "classnames"
import { useRootState } from "@/App.js"
import { request } from "@/request.js"
import ClickAway from "@/components/Utils/ClickAway"
import PatientInfo from "./PatientInfo"
import LocationInfo from "./LocationInfo"
import _ from "lodash"



const AddNewRecord = (props) => {
  const [page, setPage] = useState(0)
  const [patientInfo, setPatientInfo] = useState(null)
  const [locations,setlocations]=useState([])
  const blankrecord = {
    category: "Residence",
    dateFrom: "",
    dateTo: "",
    location: null,
  }
  const [locationRecord, setLocationRecord] = useState([{ ...blankrecord }])
  const submitForm=(l)=>{
    setLocationRecord(l)
    const filtered= _.filter(l,i=>i)
    console.log(filtered)
  }
  const onPageChange = (pageNum, p, l) => {
    if(p){
      console.log(p)
      setPatientInfo(p)
    }
    if(l){
      setLocationRecord(l)
    }
    setPage(pageNum)
  }
  return (
    <>
    {
      page === 0 ?
      <PatientInfo onPageChange={onPageChange}/>
      :
      <LocationInfo onPageChange={onPageChange} locationRecord={locationRecord} submitForm={submitForm} />

    }
    </>
  )
}

export default AddNewRecord
